var app=angular.module("galleryApp",["ngRoute"]).config(function(t){t.when("/",{templateUrl:"templates/albumList.html",controller:"AlbumListCtrl"}).when("/album/:albumName",{templateUrl:"templates/albumPage.html",controller:"AlbumCtrl"}).when("/album/:albumName/photo/:photoName",{templateUrl:"templates/photoPage.html",controller:"PhotoCtrl"}).when("/add",{templateUrl:"templates/addAlbum.html",controller:"addAlbumCtrl"}).otherwise({redirectTo:"/"})});app.factory("getTemplate",function(t){var l=function(l){t.get("/templates/newLine.html").success(l)};return{getNewLine:l}}),app.controller("AlbumListCtrl",function(t,l){t.json=l.get("json/gallery.json").success(function(l){t.albums=l})}),app.controller("AlbumCtrl",function(t,l,e){t.albumName=e.albumName,t.json=l.get("json/gallery.json").success(function(l){angular.forEach(l,function(l,e){l.title==t.albumName&&(t.album=l)})})}),app.controller("addAlbumCtrl",function(t,l){var e=function(t){$("table").append(t)};t.name="",t.src_cover="",t.pic=[],t.add=function(){console.log(t.name)},t.addNewPic=function(){l.getNewLine(e)}}),app.controller("PhotoCtrl",function(t,l,e){t.albumName=e.albumName,t.pic=e.photoName,t.json=l.get("json/gallery.json").success(function(l){angular.forEach(l,function(l,e){l.title==t.albumName&&(t.album=l.pictures)}),angular.forEach(t.album,function(l,e){l.title==t.pic&&(t.photo=l)})})});